!> @file!!  Contains a single module, @ref interception_base_class, which defines!!  an abstract class from which other interception method classes can be derived.!>  Base interception class.module interception_base_class  use iso_c_binding, only : c_short, c_int, c_float, c_double  implicit none    type, abstract, public :: T_INTERCEPTION_BASE_CLASS    private    real (kind=c_float) :: fInterception  contains      procedure(initialize_sub), deferred :: initialize      procedure(compute_fn), deferred     :: compute      procedure, public :: get => get_interception_value_fn  end type T_INTERCEPTION_BASE_CLASS  abstract interface     subroutine initialize_sub(this, fConstant_1, fConstant_2)          import :: T_INTERCEPTION_BASE_CLASS, c_float      class (T_INTERCEPTION_BASE_CLASS), intent(inout) :: this          real (kind=c_float), intent(in)                  :: fConstant_1       real (kind=c_float), intent(in)                  :: fConstant_2    end subroutine initialize_sub  end interface   abstract interface     function compute_fn(this, fRainfall, fFog)      result(fInterception)      import :: T_INTERCEPTION_BASE_CLASS, c_float      class (T_INTERCEPTION_BASE_CLASS), intent(in)        :: this      real (kind=c_float), intent(in)                      :: fRainfall      real (kind=c_float), intent(in), optional            :: fFog    end function compute_fn  end interface    integer (kind=c_int), public, parameter :: INTERCEPTION_LINEAR      = 1  integer (kind=c_int), public, parameter :: INTERCEPTION_GASH        = 2  integer (kind=c_int), public, parameter :: INTERCEPTION_EXPONENTIAL = 3  contains  function get_interception_value_fn(this)      result(fInterception)    class (T_INTERCEPTION_BASE_CLASS), intent(in)  :: this    real (kind=c_float)                 :: fInterception    fInterception = this%fInterception  end function get_interception_value_fn end module interception_base_class